library(readr)
library(reshape2) #install.packages("reshape2")
library(ggplot2) #install.packages("ggplot2")

# Set directory to file-containing folders
setwd("~/Desktop/data2/Cluster_matrix/temp_genelist/long_data")

# Read files .csv within that file
filenames <- list.files(path="~/Desktop/data2/Cluster_matrix/temp_genelist/long_data",
                        pattern="long_clusterdata+.*csv")

# Create list of data frame names without the "long_clusterdata_" and ".csv" part 
names <-substr(filenames,18,nchar(filenames)-4)

# Load all files
for(i in names){
  filepath <- file.path("~/Desktop/data2/Cluster_matrix/temp_genelist/long_data",paste("long_clusterdata_",i,".csv",sep=""))
assign(i, read.delim(filepath,
                     colClasses=c("factor",rep("numeric",2), "factor"),
                     sep = ","))
}

# List all dataframes to help manually design graphs
dataframes <- names(which(unlist(eapply(.GlobalEnv,is.data.frame))))

# Make comparative cluster graphs
irisColors <- setNames( c('lightcoral', 'steelblue', 'lightgreen'), c("F22_OA", "F22_SO", "F22_SA")  )
plot.new()
pdf("~/Desktop/data2/Cluster_matrix/clustergraph_matrixcomparisons.pdf", height=11, width=8.5)

