library(readr) library(reshape2) library(ggplot1314_2) library(ggpubr)

# Set directory to file-containing folders
setwd("~/Desktop/data2/Cluster_matrix/1314_temp_genelist/compared_to_F22_genes/long_data")

# Read files .csv within that file
filenames <- list.files(path="~/Desktop/data2/Cluster_matrix/1314_temp_genelist/compared_to_F22_genes/long_data",
                        pattern="long_clusterdata+.*csv")

# Create list of data frame names without the "long_clusterdata_" and ".csv" part 
names <-substr(filenames,18,nchar(filenames)-4)

# Load all files
for(i in names){
  filepath <- file.path("~/Desktop/data2/Cluster_matrix/1314_temp_genelist/compared_to_F22_genes/long_data",paste("long_clusterdata_",i,".csv",sep=""))
  assign(i, read.delim(filepath,
                       colClasses=c(rep("factor",2),rep("numeric",2)),
                       sep = ","))
}

# List all dataframes to help manually design graphs
dataframes <- names(which(unlist(eapply(.GlobalEnv,is.data.frame))))

